# Parse the HTML
soup = BeautifulSoup(html_content, 'html.parser')

# Extract all titles from the playlist
playlist_items = soup.select('.plItem .plTitle')
audio_files = [item.text.strip() + ".mp3" for item in playlist_items]

# Folder to save downloaded files
save_folder = "downloaded_audio"
os.makedirs(save_folder, exist_ok=True)

# Function to download audio
def download_audio(file_name):
    file_url = base_url + file_name.replace(" ", "%20")  # Replace spaces with URL encoding
    file_path = os.path.join(save_folder, file_name)
    print(f"Downloading {file_name}...")
    try:
        response = requests.get(file_url, stream=True)
        response.raise_for_status()
        with open(file_path, 'wb') as audio_file:
            for chunk in response.iter_content(chunk_size=1024):
                if chunk:
                    audio_file.write(chunk)
        print(f"Saved to {file_path}")
    except Exception as e:
        print(f"Failed to download {file_name}: {e}")

# Iterate over the audio files and download each one
for audio_file in audio_files:
    download_audio(audio_file)

print("All downloads complete!")